import { useState, useEffect } from 'react';
import './Customer.css';
import CustomerCard from './CustomerCard.js';
import CustomerForm from './CustomerForm.js';

function Customer() {

    const [customers, setCustomers] = useState([]);
    const [showForm, setShowForm] = useState(false);
    const [scopedCustomer, setScopedCustomer] = useState({});
    const [error, setError] = useState();

    useEffect(() => {
        fetch("http://localhost:8080/customer")
        .then(response => response.json())
        .then(result => setCustomers(result))
        .catch(console.log);
    }, []);

    function levelClick(event){
        if(event.target.value == ""){
            setCustomers([]);
        } else {
            fetch(`http://localhost:8080/customer/level/${event.target.value}`)
            .then(response => response.json())
            .then(result => setCustomers(result))
            .catch(error => console.log(error))
        }
    }

    function stateClick(event){
        if(event.target.value == ""){
            setCustomers([]);
        } else {
            fetch(`http://localhost:8080/customer/state/${event.target.value}`)
            .then(response => response.json())
            .then(result => setCustomers(result))
            .catch(error => console.log(error))
        }
    }





    function addClick() {
        setScopedCustomer({ id: 0, firstName: "", lastName: "", street:"", city:"", state:"", zipCode:"", level:""});
        setShowForm(true);
    }

    function notify({ action, customer, error }) {

        if (error) {
            setError(error);
            setShowForm(false);
            return;
        }

        switch (action) {
            case "add":
                setCustomers([...customers, customer]);
                break;
            case "edit":
                setCustomers(customers.map(e => {
                    if (e.id === customer.id) {
                        return customer;
                    }
                    return e;
                }));
                break;
            case "edit-form":
                setScopedCustomer(customer);
                setShowForm(true);
                return;
            case "delete":
                setCustomers(customers.filter(e => e.id !== customer.id));
                break;
            case "level":

        }

        setError("");
        setShowForm(false);
    }

    if (showForm) {
        return <CustomerForm customer={scopedCustomer} notify={notify} />
    }

    return (
        <>
            {error && <div className="alert alert-danger">{error}</div>}
            <div>
                <h1 id='customerTitle'>Customers</h1>
                <button className="btn btn-primary" type="button" onClick={addClick}>Add a Customer</button>
                <select nameonChange={levelClick}>
                    <option>Get Customer by Level</option>
                    <option>Gold</option>
                    <option>Silver</option>
                    <option>Bronze</option>
                </select>
                <select onChange={stateClick}>
                    <option>Get Customer by State</option>
                    <option>Gold</option>
                </select>

                <table id='customer'>
                    <tr>
                        <th>First Name</th>
                        <th>Last Name</th>
                        <th>Street</th>
                        <th>City</th>
                        <th>State</th>
                        <th>Zip Code</th>
                        <th>Level</th>
                    </tr>
                    <tbody>
                        {customers.map(r => <CustomerCard key={r.customerId} customer={r} notify={notify} />)}
                    </tbody>
                </table>
            </div>
        </>
    )
}

export default Customer;
