import { useState } from 'react';

function GameForm({ game: initialGame, notify }) {

    const [game, setGame] = useState(initialGame);
    const isAdd = initialGame.id === 0;

    function handleChange(evt) {
        const clone = { ...game };
        clone[evt.target.name] = evt.target.value;
        setGame(clone);
    }

    function handleSubmit(evt) {
        evt.preventDefault();

        const url = isAdd ? "http://localhost:8080/games" : `http://localhost:8080/games${game.id}`;
        const method = isAdd ? "POST" : "PUT";
        const expectedStatus = isAdd ? 201 : 204;

        const init = {
            method,
            headers: {
                "Content-Type": "application/json",
                "Accept": "application/json"
            },
            body: JSON.stringify(game)
        };

        fetch(url, init)
            .then(response => {
                if (response.status === expectedStatus) {
                    if (isAdd) {
                        return response.json();
                    } else {
                        return game;
                    }
                }
                return Promise.reject(`Didn't receive expected status: ${expectedStatus}`);
            })
            .then(result => notify({
                action: isAdd ? "add" : "edit",
                game: result
            }))
            .catch(error => notify({ error: error }));

    }

    return (
        <>
            <h1>{game.id > 0 ? "Edit" : "Add"} Game</h1>
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label htmlFor="title">Title</label>
                    <input type="text" id="title" name="title"
                        className="form-control"
                        value={game.title} onChange={handleChange} />
                </div>
                <div className="mb-3">
                    <label htmlFor="description">Description</label>
                    <input type="text" id="description" name="descriptiont"
                        className="form-control"
                        value={game.description} onChange={handleChange} />
                </div>
                <div className="mb-3">
                    <label htmlFor="price">Price</label>
                    <input type="text" id="price" name="price"
                        className="form-control"
                        value={game.price} onChange={handleChange} />
                </div>
                <div className="mb-3">
                    <label htmlFor="studio">Studio</label>
                    <input type="text" id="studio" name="studio"
                        className="form-control"
                        value={game.state} onChange={handleChange} />
                </div>
                <div className="mb-3">
                    <label htmlFor="zipCode">Quantity</label>
                    <input type="text" id="zipCode" name="zipCode"
                        className="form-control"
                        value={game.zipCode} onChange={handleChange} />
                </div>
                <div className="mb-3">
                    <select htmlFor="rating" name="rating" on onChange={handleChange} value={game.esrbRating}>
                    <option>Esrb Rating</option>
                    <option>E</option>
                    <option>E10+</option>
                    <option>T</option>
                    <option>M</option>
                    <option>AO</option>
                    <option>RP</option>
                </select>
                </div>
                <div className="mb-3">
                    <button className="btn btn-primary mr-3" type="submit">Save</button>
                    <button className="btn btn-secondary" type="button" onClick={() => notify({ action: "cancel" })}>Cancel</button>
                </div>
            </form>
        </>
    );
}

export default GameForm;
