import Game from './components/Game/Game.js';
import Console from './components/Console/Console.js';
import Tshirt from './components/TShirt/TShirt.js';

function Home()  {
  function inventories(event){
    if(event.target.value === "Game"){
     fetch(<Game />)
    } else if (event.target.value === "Console"){
        <Console />
    } else if (event.target.value === "Tshirt"){
      <Tshirt />
  }

  }

    return (
      <>
      <div>
        <h1 className="text-center">Welcome to DPC's M4-Summative-Project</h1>
        <h2 className="text-center">Check out our inventories:</h2>
        <div className="text-center">
                    <select name="inventory" onChange={inventories}>
                    <option>Choose inventories</option>
                    <option>Game</option>
                    <option>Console</option>
                    <option>Tshirt</option>
                </select>

                </div>
      </div>
      </>
    );
  };

  export default Home;
